    private void getMenu(){
        String menuFilePath = "menus.txt";
        java.lang.reflect.Type menuType = new TypeToken<Menu>(){}.getType();
        FileHandler<Menu> MenuFile = new FileHandler<>();
        List<Menu> menuList = MenuFile.readTxt(menuFilePath, menuType);
        for (Menu mn:menuList){
            IDList.add(mn.itemID);
        }
    }
    private void showTB(){
        DefaultTableModel model = (DefaultTableModel)CustomerReviewTB.getModel();
        String reviewFilePath = "reviews.txt";
        ArrayList<Object[]> tbcontent = new ArrayList<>();
        java.lang.reflect.Type reviewType = new TypeToken<Review>(){}.getType();
        FileHandler<Review> reviewFile = new FileHandler<>();
        List<Review> ReviewList = reviewFile.readTxt(reviewFilePath, reviewType);
        for (Review rv:ReviewList){
            System.out.println(rv.reviewedEntityID);
            System.out.println(IDList.contains(rv.reviewedEntityID));
            if(IDList.contains(rv.reviewedEntityID)){
                String starRating = getStars(rv.rating);
                tbcontent.add(new Object[]{rv.customerID,starRating,rv.comment});
            }
        }
        for (Object[] line:tbcontent){
            System.out.println(line.length);
            model.addRow(line);
        }
    }
    private String getStars(int rating) {
    return "â˜…".repeat(Math.max(0, rating)); // Generates stars based on rating
}
    

