    private void showTB(String ID){
        DefaultTableModel model = (DefaultTableModel)modifyTB.getModel();
        model.setRowCount(0);
        String menuFilePath = "menus.txt";
        java.lang.reflect.Type menuType = new TypeToken<Menu>(){}.getType();
        FileHandler<Menu> MenuFile = new FileHandler<>();
        List<Menu> menuList = MenuFile.readTxt(menuFilePath, menuType);
        IDList = new ArrayList<>();
        ArrayList<String> itemList = new ArrayList<>();
        ArrayList<Double> priceList = new ArrayList<>();
        ArrayList<String> statusList = new ArrayList<>();
        DecimalFormat df = new DecimalFormat("0.00");
        for(Menu item:menuList){
            if (!IDList.contains(item.itemID)&& ID.equals(item.vendorID)){
                IDList.add(item.itemID);
                itemList.add(item.itemName);
                priceList.add(item.price);
                if(item.status == null){
                    statusList.add("Unknown");
                }
                else{
                    statusList.add(item.status);
                }
            }
        }
        ArrayList<Object[]> tbcontent = new ArrayList<>();
        for (int i = 0; i < IDList.size(); i++) {
            tbcontent.add(new Object[]{IDList.get(i),itemList.get(i),df.format(priceList.get(i)),statusList.get(i)});
            //tbcontent.add(new Object[]{IDList.get(i),itemList.get(i),quantity.get(i),priceList.get(i),amountList.get(i)});
        }
        System.out.println(tbcontent);
        for (Object[] line:tbcontent){
            model.addRow(line);
        }
    }
