    private void createBTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_createBTActionPerformed
        String menuFilePath = "menus.txt";
        java.lang.reflect.Type menuType = new TypeToken<Menu>(){}.getType();
        FileHandler<Menu> MenuFile = new FileHandler<>();
        List<Menu> menuList = MenuFile.readTxt(menuFilePath, menuType);
        String valueToCheck = itemIDTB.getText();
        System.out.println(menuList);
        if(!IDList.contains(valueToCheck)){
            System.out.println("?");
            menuList.add(new Menu(VendorID,itemIDTB.getText(),itemNameTB.getText(),Double.parseDouble(priceTB.getText())));
            MenuFile.writeTxt(menuFilePath, menuList); 
        }
        showTB(VendorID);
    }//GEN-LAST:event_createBTActionPerformed

    private void DeleteBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DeleteBtnActionPerformed
        String menuFilePath = "menus.txt";
        java.lang.reflect.Type menuType = new TypeToken<Menu>(){}.getType();
        FileHandler<Menu> MenuFile = new FileHandler<>();
        List<Menu> menuList = MenuFile.readTxt(menuFilePath, menuType);
        if (modifyTB.getSelectedRow() != -1) {
            String ItemID = modifyTB.getValueAt(modifyTB.getSelectedRow(), 0).toString();
            menuList.removeIf(menu -> menu.itemID.equals(ItemID));
        }   
        MenuFile.writeTxt(menuFilePath, menuList);
        showTB(VendorID);
    }//GEN-LAST:event_DeleteBtnActionPerformed

    private void modifyTBMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_modifyTBMouseClicked
        if (modifyTB.getSelectedRow() != -1) {
            itemIDTB.setText(modifyTB.getValueAt(modifyTB.getSelectedRow(), 0).toString());
            itemNameTB.setText(modifyTB.getValueAt(modifyTB.getSelectedRow(), 1).toString());
            priceTB.setText(modifyTB.getValueAt(modifyTB.getSelectedRow(), 2).toString());
        }   
    }//GEN-LAST:event_modifyTBMouseClicked

    private void SaveBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SaveBTNActionPerformed
        String menuFilePath = "menus.txt";
        java.lang.reflect.Type menuType = new TypeToken<Menu>(){}.getType();
        FileHandler<Menu> MenuFile = new FileHandler<>();
        List<Menu> menuList = MenuFile.readTxt(menuFilePath, menuType);
        if(IDList.contains(itemIDTB.getText())){
            menuList.removeIf(menu -> menu.itemID.equals(itemIDTB.getText()));
            menuList.add(new Menu(VendorID,itemIDTB.getText(),itemNameTB.getText(),Double.parseDouble(priceTB.getText())));
            MenuFile.writeTxt(menuFilePath, menuList);
        }
        showTB(VendorID);
    }//GEN-LAST:event_SaveBTNActionPerformed

